public class Foo {
  private int x;
  public void bar() {
    switch (x) {
      case 1: {
        System.out.println("I am a fish.");
        System.out.println("I am a fish.");
        System.out.println("I am a fish.");
        System.out.println("I am a fish.");
        break;
      }

      case 2: {
        System.out.println("I am a cow.");
        System.out.println("I am a cow.");
        System.out.println("I am a cow.");
        System.out.println("I am a cow.");
        break;
      }
    }
  }
}


public class SeniorClass {
  public SeniorClass(){
      toString(); //may throw NullPointerException if overridden
  }
  public String toString(){
    return "IAmSeniorClass";
  }
}
public class JuniorClass extends SeniorClass {
  private String name;
  public JuniorClass(){
    super(); //Automatic call leads to NullPointerException
    name = "JuniorClass";
  }
  public String toString(){
    return name.toUpperCase();
  }
}


public class OuterClass {
  void method(){
    InnerClass ic = new InnerClass();//Causes generation of accessor
  }
  public class InnerClass {
    private InnerClass(){
    }
  }
}

public class OuterClass {
  void method(){
    InnerClass ic = new InnerClass();//OK, due to public constructor
  }
  public class InnerClass {
    public InnerClass(){
    }
  }
}

public class OuterClass {
  void method(){
    InnerClass ic = InnerClass.getInnerClass();//OK
  }
  public static class InnerClass {
    private InnerClass(){
    }
    private static InnerClass getInnerClass(){
      return new InnerClass();
    }
  }
}

public class OuterClass {
  private OuterClass(){
  }
  public class InnerClass {
    void method(){
      OuterClass oc = new OuterClass();//Causes generation of accessor
    }
  }
}


