rule.example:
public class Foo {
   protected void finalize() {}
}


public class Foo {
   protected void finalize() {
     super.finalize();
   }
}

public class Foo {
   // this is confusing and probably a bug
   protected void finalize(int a) {
   }
}

public class Foo {
   protected void finalize() {
       something();
       // neglected to call super.finalize()
   }
}


public class Foo {
 public void finalize() {
  // do something
 }
}

public class Foo {
 void foo() {
  Bar b = new Bar();
  b.finalize();
 }
}
