public class Foo {
	public void bar() throws SQLException
	{
		DAOTransaction = null;
		Connection cnx = null;
		ResultSet rs = null;
		Statement stmt = null;

		try
		{
			// ...
		}
		finally
		{
			rs.close(); // Correct
			//stmt.close(); // Error !!! you have to close the Statement
			cnx.commit(); // Error !!! You have to use close() before doing this !
			DAOTransaction.commit(); // Correct
		}
	}
}

